import React from "react";
import "./Main.css"

export default class Main extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            btnShow: false
        }
    }
    select = (id) => {
        return (e) =>{
            this.props.selectTodo(id);
        }
    }
    mouseEnterHandleEvent = () => {
        this.setState({
            btnShow: true
        })
    }
    mouseLeaveHandleEvent = () => {
        this.setState({
            btnShow: false
        })
    }
    render() {
        return(
            <ul className="todo-main">
                {
                    this.props.list.map((item) => {
                        return(
                            <li key={item.id} onMouseEnter={this.mouseEnterHandleEvent} onMouseLeave={this.mouseLeaveHandleEvent}>
                                <label>
                                    <input type="checkbox" checked={item.done} onChange={this.select(item.id)}/>
                                    <span>{item.name}</span>
                                </label>
                                <button className="btn btn-danger" style={{display: this.state.btnShow}}>删除</button>
                            </li>
                        )
                    })
                }
            </ul>
        )
    }
}
