import React from "react";
import "./Main.css"
import Item from "./childs/Item/Item";

export default class Main extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            btnShow: false
        }
    }
    select = (id) => {
        return (e) =>{
            this.props.selectTodo(id);
        }
    }
    mouseEnterHandleEvent = () => {
        this.setState({
            btnShow: true
        })
    }
    mouseLeaveHandleEvent = () => {
        this.setState({
            btnShow: false
        })
    }
    render() {
        return(
            <ul className="todo-main">
                {
                    this.props.list.map((item) => {
                        return(
                            <Item item={item}/>
                        )
                    })
                }
            </ul>
        )
    }
}
